#!/bin/bash
set -xeuo pipefail

echo "Deploy test..."

curl -v --header "Content-Type: application/json" --data '{"method":"eth_blockNumber","id":1001,"jsonrpc":"2.0","params":[]}' $PROXY_URL
# Check error response on "Invalid Ethereum transaction nonce" while deploying a contract
# https://github.com/neonlabsorg/proxy-model.py/issues/147
RESPONSE=$(curl --header 'Content-Type: application/json' --data '{"id":147001,"jsonrpc":"2.0","method":"eth_sendRawTransaction","params":[""]}' $PROXY_URL)
python3 -c "
import sys
arg=sys.argv[1]
print(arg)
import json
response = json.loads(arg)
print('error:', response['error'])
print('code:', response['error']['code'])
assert response['error']['code'] == -32002
substring = 'Invalid Ethereum transaction nonce:'
print('substring:', substring)
logs = response['error']['data']['logs']
print('logs:', logs)
log = [e for e in logs if substring in e][0]
print('log', log)
assert len(log) > len(substring)
file_name = 'src/entrypoint.rs'
print('file_name', file_name)
assert file_name in log
" "$RESPONSE"


# Check eth_estimateGas on deploying a contract
# https://github.com/neonlabsorg/proxy-model.py/issues/122
RESPONSE=$(curl -v --header "Content-Type: application/json" --data '{"method":"eth_estimateGas","id":1002,"jsonrpc":"2.0","params":[{"from":"0x55864414d401c9ff160043c50f6daca3bd22ccfc", "value": "0x0", "data":""}]}' $PROXY_URL)
python3 -c "
import sys
arg=sys.argv[1]
print(arg)
import json
resp = json.loads(arg)
print('used_gas:', resp['result'])
assert resp['result'] == 89078
" "$RESPONSE"

# Check eth_estimateGas on deploying a contract with the empty value
# https://github.com/neonlabsorg/proxy-model.py/issues/122
RESPONSE=$(curl -v --header "Content-Type: application/json" --data '{"method":"eth_estimateGas","id":1002,"jsonrpc":"2.0","params":[{"from":"0x55864414d401c9ff160043c50f6daca3bd22ccfc", "data":""}]}' $PROXY_URL)
python3 -c "
import sys
arg=sys.argv[1]
print(arg)
import json
resp = json.loads(arg)
print('used_gas:', resp['result'])
assert resp['result'] == 89078
" "$RESPONSE"

# Check eth_estimateGas on deploying a contract with the empty data
# https://github.com/neonlabsorg/proxy-model.py/issues/122
RESPONSE=$(curl -v --header "Content-Type: application/json" --data '{"method":"eth_estimateGas","id":1002,"jsonrpc":"2.0","params":[{"from":"0x55864414d401c9ff160043c50f6daca3bd22ccfc", "value": "0x0"}]}' $PROXY_URL)
python3 -c "
import sys
arg=sys.argv[1]
print(arg)
import json
resp = json.loads(arg)
print('used_gas:', resp['result'])
assert resp['result'] == 53001
" "$RESPONSE"

# Check eth_estimateGas on deploying a contract with the empty data and value
# https://github.com/neonlabsorg/proxy-model.py/issues/122
RESPONSE=$(curl -v --header "Content-Type: application/json" --data '{"method":"eth_estimateGas","id":1002,"jsonrpc":"2.0","params":[{"from":"0x55864414d401c9ff160043c50f6daca3bd22ccfc"}]}' $PROXY_URL)
python3 -c "
import sys
arg=sys.argv[1]
print(arg)
import json
resp = json.loads(arg)
print('used_gas:', resp['result'])
assert resp['result'] == 53001
" "$RESPONSE"

echo "Deploy test success"
exit 0
